import React from 'react'
import { Card, Avatar,Row,Col,Button,Image } from 'antd';
import { Link } from 'react-router-dom';
import products from '../../products';
import Rating from '../../components/rating/Rating';
import  {FC} from "react";
import { PATHS } from '../../core/enums/paths';
import {useParams} from 'react-router-dom';
import Product from '../../components/product/Product';

const { Meta } = Card;
{/*return (


 duleconst ProductScreen = ({match}) => {
    const product = products.find(p => p._id ===match.params.id)
   
        <div>
            {product}
        </div>
    )
}


const product =  ( products.find(p => p._id ===props.product._id))
const ProductScreen = ({match}: {match:any}) => {
    
       
    return (
       
        <>
        product
        <a href={PATHS.HOME} className="btn "> back list</a>
                <h1>okey</h1>
        </>
  
  
 )
}


