import  {FC} from "react";
import React, { useState, useEffect } from 'react'
import { Card, Avatar,Row,Col,Button,Image,Select } from 'antd';
import { Link } from 'react-router-dom';
import Rating from '../../components/rating/Rating';
import {useDispatch, useSelector} from 'react-redux'
import { PATHS } from '../../core/enums/paths';
import {useParams} from 'react-router-dom';
import { listProductDetails } from '../../actions/productActions';
import { Alert } from 'antd';
import Loader from '../../components/loader/Loader';
import './ProductScreen.scss'


const ProductScreen = ({history,match}  ) => {
    const [qty, setQty] = useState(0)
   
    const dispatch = useDispatch();
    const { id } = useParams();

    const productDetails = useSelector(state => state.productDetails)
    const{ loading,error,product} = productDetails

    const { Option, OptGroup } = Select;
    const { Meta } = Card;
  

    useEffect(() => {
        
     dispatch(listProductDetails(id))
     }

    , [dispatch, match])

    const addToCartHandler = () => {
        history.push(`/cart/${match.params.id}?qty=${qty}`)
    }
   
    return (
        <>
             <a href={PATHS.HOME}> Back to home</a> 
             { loading ? (
                 <Loader />
                ) : error ? (
                    <Alert  message={error} type="error"> </Alert>  
                ) :  (
                    <div>
                        <Row  className="product" >
                            <Col className="gutter-row "  >
                                <Card
                                    hoverable
                                    style={{ width: 300 }}
                                    cover={<img alt={product.name} 
                                    src={product.image} />}
                            
                                >
                                    
                                    <Meta title={product.name} description={product.description} />
                                
                                <div className="rating col-3">
                                        {product.rating} from {product.numReviews} reviews

                                </div>
                                <div>
                                    <h2>Price : ${product.price}</h2>
                                
                                
                                <h3> Status: {product.countInStock > 1 ? 'In stock' : 'Out of the stock'}</h3>
                                </div>
                                {product.countInStock > 0 && (
                                    <Row>
                                        <Col>Quantity</Col>
                                        <Col> 
                                        <Select defaultValue={qty}  
                                        onChange={(e) => setQty(e.target.value)}  
                                        style={{ width: 50 }} >
                                            {
                                            [...Array(product.countInStock).keys()].map(
                                                (x) => (
                                                    <Option key={x + 1} value={x + 1} >{x+1} </Option>
                                                )
                                            )}
                                            {/* [0,1,2,3] si il y'a 3=countInStock */}
                                           
                                        </Select>
                                        </Col>
                                    </Row>
                                )}


                                <Button onClick={addToCartHandler} className="btn-add-to-card" disabled={product.countInStock === 0} >Add to card</Button>

                                </Card>
                            </Col>
                        </Row>
                    </div>
                
                )
            }
              
        
           
        </>
    )
}

export default ProductScreen
