const { Meta } = Card;




type product={
    _id: string;
    name: string;
    image: string;
    description: string;
    brand: string;
    category: string;
    price: number;
    countInStock: number;
    rating: number;
    numReviews: number;
};

const Product: React.FC<product> = ({ _id, name, image, description,brand,category,price,countInStock,rating,numReviews}) => {
 
  
    return (
        <>
         <h2>{props.name}</h2>
         <p> {props.image} </p>
         <h2>{props._id}</h2>
                  <h3>{props.description}</h3>
           {/*} <Card
                style={{ width: 300 }}
                cover={
                <img
                    alt={props.name}
                    src={props.image} 
                />
                }
                actions={[
                <SettingOutlined key="setting" />,
                <EditOutlined key="edit" />,
                <EllipsisOutlined key="ellipsis" />,
                ]}
            >
                <h2>{props.name}</h2>
                <a href={`/product/${props._id}`}>  <img
                    alt={props.name}
                    src={props.image}
                /></a>
                <Meta
                avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                title={props.name}
                description={props.description}
                />
            </Card>*/}
              
        </>
    )
}


export default Product
