import  {FC} from "react";
import React, { useState, useEffect } from 'react'
import {  Avatar,Select} from 'antd';
import { Link } from 'react-router-dom';
import Rating from '../../components/rating/Rating';
import {useDispatch, useSelector} from 'react-redux'
import { PATHS } from '../../core/enums/paths';
import {useParams} from 'react-router-dom';
import { listProductDetails } from '../../actions/productActions';
import { Alert } from 'antd';
import Loader from '../../components/loader/Loader';
import './ProductScreen.scss';
import Form from "antd/lib/form/Form";
import {Card,ListGroup,Row,Col,Button,Image} from 'react-bootstrap';



const ProductScreen = ({match}  ) => {
    const [qty, setQty] = useState(0)

    const dispatch = useDispatch();
    const { id } = useParams();

    const productDetails = useSelector(state => state.productDetails)
    const{ loading,error,product} = productDetails
  

    useEffect(() => {
        
     dispatch(listProductDetails(id))
     }

    , [dispatch, match])

   
    return (
        <>
             <a href={PATHS.HOME}> Back to home</a> 
             { loading ? (
                 <Loader />
                ) : error ? (
                    <Alert  message={error} type="error"> </Alert>  
                ) :  (
                    <div>
                        <Row  className="product" >
                            <Col className="gutter-row "  >
                                <Card
                                    hoverable
                                    style={{ width: 300 }}
                                    cover={<img alt={product.name} 
                                    src={product.image} />}
                            
                                >
                                    
                                    <Meta title={product.name} description={product.description} />
                                
                                <div className="rating col-3">
                                        {product.rating} from {product.numReviews} reviews

                                </div>
                                <div>
                                    <h2>${product.price}</h2>
                                </div>
                                <p>Status:</p>
                                <p> {product.countInStock > 1 ? 'In stock' : 'Out of the stock'}</p>

                                {product.countInStock > 0 && (
                                    <ListGroup.Item>

                                 
                                    <Row>
                                        <Col>Quantity</Col>
                                        <Col>
                                            <Form.Control
                                                as="select"
                                                value={qty}
                                                onChange={(e) => setQty(e.target.value)}>
                                                    {
                                                        [...Array(product.countInStock).keys()].map((x) =>(
                                                            <option key={x+1} value={x+1}>
                                                                {x+1}

                                                            </option>
                                                        ))
                                                    }
                                            </Form.Control>
                                          {/*}
                                                    <Form.Item name="prefix" noStyle>
                                                    <Select
                                                        style={{
                                                        width: 70,
                                                        }}
                                                    >
                                                        <Option value="86">+86</Option>
                                                        <Option value="87">+87</Option>
                                                    </Select>
                                                    </Form.Item>*/}
                                        
                                        </Col>
                                    </Row>
                                    </ListGroup.Item>
                                )}


                                <Button href="" className="btn-add-to-card" >Add to card</Button>

                                </Card>
                            </Col>
                        </Row>
                    </div>
                
                )
            }
              
        
           
        </>
    )
}

export default ProductScreen
