const {id}  = useParams();


const ProductScreen = () => {

    const [product, setProduct] = useState({});

    useEffect(() => {
        
        //function for search products
        const fetchProduct = async () =>{
            
            //searching data
            const {data} = await axios.get(`/api/products/${id}`) //axios yejbedha men server.js path heki

            //send data to the empty array that i declared detProducts
            setProduct(data)
     }

     fetchProduct()
    },[])


 
   
   //const product = products.find(p => p._id ===id)
   //const nb =  product?.countInStock
