    return (
        <>
        <div className="productScreen">
           <Row  className="product" >
                <Col className="gutter-row "  >
                    <Card
                        hoverable
                        style={{ width: 300 }}
                        cover={<img alt={product?.name} 
                        src={product?.image} />}
                
                    >
                        
                        <Meta title={product?.name} description={product?.description} />
                    
                    <div className="rating col-3">
                            {product?.rating} from {product?.numReviews} reviews

                    </div>
                    <div>
                        <h2>${product?.price}</h2>
                    </div>
                    <p>Status:</p>
                    <p> {test? 'okkkk Instock' : 'niiiiine'}
                        
                        
                    " In stock" or "out of stock" </p>
                    <Button href="">Add to card</Button>
                    </Card>
                </Col>
            </Row>
        </div>
        <Row className="product">
            <Col sm={12} md={12} className="product-image">
                <Image src={product?.image} width={400}   />
              
   
            </Col>
        </Row>
       
           <h1> produit</h1>
             <ul>
                 <li>  name: {product?.name}   </li>
                 <li>  id: {product?._id}   </li>
                 <li>  description: {product?.description}   </li>
                 <li> {product?.image}" </li>
                 <li>  numReviews: {product?.numReviews}   </li>
             </ul> 
             <a href={PATHS.HOME}> Back to home</a> 
         
              
        
           
        </>
    )
}

export default ProductScreen
