import React , {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../../components/loader/Loader';
import { login } from '../../actions/userActions';
import FormLogin from '../../components/formLogin/FormLogin';
import { Form, Input, Button, Checkbox } from 'antd';
import { Alert } from 'antd';
import { useLocation } from 'react-router';
//import { useHistory } from "react-router-dom";
import {useNavigate} from 'react-router-dom';

    
//let history = useHistory();
const history = useNavigate();

const LoginScreen = ({history}) => {
    const location = useLocation();
     
    const [email , setEmail] = useState('')
    const [password , setPassword] = useState('')

    

    const userLogin = useSelector(state => state.userLogin)
    const { loading, error, userInfo } = userLogin //this variables from the userReducers

   

    const redirect = location.search ? location.search.split('=')[1] : '/'
  


    useEffect (() => {
        if(userInfo){
          history(redirect)
            //history.push(redirect)
        }
    },[history, userInfo, redirect]) //because when they change it we want to redirect again

    const dispatch = useDispatch()
   

      function submitHandler(e) {
    
    
      e.preventDefault();
      //dispatchlogin
      dispatch(login(email, password)) //passing email and password from the form
   
    
       
    }
    {/* const dispatch = useDispatch()
   const submitHandler =  (e) => {
       // if (e && e.preventDefault) { // add?
     {/** 
       try{
            e.preventDefault();
           // e.persist();
            dispatch(login(email, password))
            console.log('yes')
        }
      catch(error){
        console.log(error)
      }
      try{ e.preventDefault();
      dispatch(login(email, password))
      console.log('yes')}
      catch(error){ console.log(error)}
     } */}
    
    //**************form********************************
      

    
      const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
      };

  return (
      <>
      <h1>Sign in</h1>
     
    
        
        <FormLogin>
        <Form
      name="basic" labelCol={{span: 8,}}
      wrapperCol={{span: 16,}}
      initialValues={{remember: true,}}
     // onFinish={submitHandler}
      //onFinishFailed={onFinishFailed}
      autoComplete="off"
      onChange={submitHandler}
    >
    
      <Form.Item name={['user', 'email']} label="Email" rules={[{ type: 'email', required: true,
            message: 'Please input your username!', }]}>
        <Input />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[
          {
            required: true,
            message: 'Please input your password!',
          },
        ]}
      >
        <Input.Password />
      </Form.Item>

      <Form.Item
        name="remember"
        valuePropName="checked"
        wrapperCol={{
          offset: 8,
          span: 16,
        }}
      >
        <Checkbox>Remember me</Checkbox>
      </Form.Item>

      <Form.Item
        wrapperCol={{
          offset: 8,
          span: 16,
        }}
      >
        <Button  variant="primary" htmlType="submit" type="primary">
          Sign In
        </Button>
      </Form.Item>
    </Form>

        <h1>New Customer?{` `} 
        <a href={redirect ? `/register?redirect=${redirect}` : '/register'}>
            Register
        </a>
        </h1>
    </FormLogin>
  </>
 
  )}

export default LoginScreen
